  KEY `index_concern_user` (`userId`) USING HASH,
  KEY `index_concern_model` (`moduleId`) USING HASH
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='我的关注,包含了我关注的好友和借款等\r\n           ';

