/*!50003 CREATE  PROCEDURE `p_amount_withdraw_auth`(IN `in_uid` bigint,IN `in_aid` bigint,IN `in_wid` bigint,IN `in_money` decimal(18,2),IN `in_poundage` decimal(18,2),IN `in_status` int,IN `in_ip` varchar(20),IN `in_remark` varchar(2048),IN `in_date` varchar(30),OUT `out_ret` int,OUT `out_desc` varchar(255))
_return:begin



	declare	t_freeze_amount	decimal(18,2) default	0;	#冻结金额

	declare	_error	int	default	0;	#异常标记

	declare continue handler for sqlexception set _error	=	1;	#sqlexception返回值



	set out_ret = -1;

	set out_desc = '执行失败';

	set	@wid	=	-1;

	set	@withDrawSum	=	0;

	set	@poundage	=	0; 

  set	@wStatus = 5;

	set	@userId	=	0;

	set	@version	=	0;

	select id,sum,	poundage,	`status`,	userId,	versoin into	@wid,	@withDrawSum,	@poundage,	@wStatus,	@userId,	@version

	from t_withdraw a where id = in_wid;

	set	@wStatus = ifnull(@wStatus,5);

