DELIMITER $$

/*!50003 CREATE  PROCEDURE `p_borrow_debt_action`(IN `in_debt_id` bigint,IN `in_auction_id` bigint,IN `in_auctioner_id` bigint, out out_ret int, out out_desc varchar(100))
_return:begin

