/*!50003 CREATE  PROCEDURE `p_borrow_initialization`(IN in_uid bigint, IN in_borrow_title varchar(200), IN in_img_path varchar(200), IN in_borrow_way int, IN in_borrow_info varchar(2048), IN in_deadline int, IN in_payment_mode int, IN in_borrow_amount decimal(18, 2), IN in_annual_rate decimal(18, 2), IN in_min_tendered_sum decimal(18, 2), IN in_max_tendered_sum decimal(18, 2), IN in_raise_term decimal(18, 2), IN in_detail varchar(2048), IN in_trade_type int, IN in_publisher bigint, IN in_excitation_type int, IN in_excitation_sum decimal(18, 2), IN in_excitation_mode int, IN in_purpose int, IN in_haspwd int, IN in_investpwd varchar(50), IN in_publish_time datetime, IN in_publiship varchar(100), IN in_isdaythe int, IN in_smallest_flow_unit decimal(18, 2), IN in_circulation_number int, IN in_nid_log varchar(50), IN in_frozen_margin decimal(18, 2), IN in_url varchar(200), IN in_bathpath varchar(200), IN in_cost_fee decimal(18, 2),  IN in_feelog varchar(2048),IN in_feestate varchar(2048),IN in_agent varchar(1024),IN in_counter_agent varchar(1024),IN in_business_detail varchar(1024),IN in_assets varchar(1024),IN in_money_purposes varchar(1024),IN in_borrow_show int , OUT out_ret int, OUT out_desc varchar(100))
_return: begin
	#发布借款
	declare in_borrow_credit bigint default 0;
	declare t_content varchar(1024)	default	'';	#信息
	declare	t_mail_template	varchar(2048)	default	'';	#站内信模版
	declare	t_email_template	varchar(2048)	default	'';	#邮件模版
	declare	t_sms_template	varchar(2048)	default	'';	#短信模版
	declare	_error	int	default	0;	#异常标记
	declare continue handler for sqlexception set _error	=	1;	#sqlexception返回值

	set out_ret = -1;
	set out_desc = '执行失败';

	#用户状态 1、启用 2、禁用 3.黑名单
	set @user_enable = 2;
	set @vipstatus = 1;
	set	@authstep	=	1;
	select `enable`, vipStatus,	authStep ,lastIP into @user_enable, @vipstatus,	@authstep ,@lastIP from t_user where id = in_uid;

	if @user_enable is null then
		set out_ret = -2;
