		leave _return;
	elseif @user_enable = 2 then
		set out_ret = -3;
		set out_desc = '已禁用用户';

		leave _return;
	elseif @user_enable = 3 then
		set out_ret = -4;
		set out_desc = '黑名单用户';

		leave _return;
	end if;

	#用户VIP状态
	if @vipstatus = 1 then
		set out_ret = -5;
		set out_desc = '非VIP不能发布借款';

		leave _return;
	elseif @vipstatus = 3 then
		set out_ret = -6;
		set out_desc = 'VIP会员过期待续费';

		leave _return;
	end if;

	#用户认证状态
	if	@authstep	=	1 then
		set out_ret = -7;
		set out_desc = '未通过个人信息认证';

		leave _return;
	elseif	( @authstep	=	2	and in_borrow_way not in (1,2,6) )then
