/* Procedure structure for procedure `p_borrow_join` */

/*!50003 DROP PROCEDURE IF EXISTS  `p_borrow_join` */;

DELIMITER $$

/*!50003 CREATE  PROCEDURE `p_borrow_join`(IN `in_bid` bigint,IN `in_uid` bigint,IN `in_basepath` varchar(255),IN `in_invest_amount` decimal(18,2),IN `in_invest_time` datetime, IN `in_status` int , IN `in_num` int , out out_ret int, out out_desc varchar(100))
_return:BEGIN
	#投资标旳
	declare	t_mail_template	varchar(2048)	default	'';
	declare	t_email_template	varchar(2048)	default	'';
	declare	t_sms_template	varchar(2048)	default	'';
	declare t_content varchar(1024)	default	'';
	declare	_error	int	default	0;
	declare continue handler for sqlexception set _error	=	1;


	set out_ret = -1;  
	set out_desc = '执行失败';	

	set @user_usableSum = null; #可用余额是否符合投标金额
	set @dealpwd = ''; #交易密码是否正确
	select usableSum, dealpwd into @user_usableSum, @dealpwd from t_user where id = in_uid;

	if @user_usableSum is null then

		set out_ret = -2;
		set out_desc = '用户不存在';

		leave _return;
	end if;

	#if(in_dealpwd != @dealpwd) then
	#	set out_ret = -4;
	#set out_desc = '交易密码错误';

	#leave _return;
	#end if;
	#招标中的借款才可以进行投标
	set @id = 0;
	set @t_frozen_margin = 0;
	set	@borrowAmount = 0;
	set @hasInvestAmount = 0;
	set @borrowStatus = 1;
	set @minTenderedSum = null;
	set @maxTenderedSum = null;
	set @publisher = -1;
  set @version = 0;
	set @borrowTitle = null;
	set @borrowWay  =1;
	set @auditTime = null;
	set	@excitationType=0;
	set @excitationSum =0;
	set @circulationNumber =0;
	set @hasCirculationNumber = 0;
	set @annualRate =null;
	set @monthRate = 0;
	set @t_deadline =0;
	set @isDayThe =1;
	set @borrowerName = null; 
	set @totalInterest = 0; #流转标应得本息
	set	@subscribe_status =	2;
	set	@smallestFlowUnit = 0;
	#select id, borrowAmount, hasInvestAmount, borrowStatus, minTenderedSum, maxTenderedSum, publisher,version,borrowTitle
	#into @id, @borrowAmount, @hasInvestAmount, @borrowStatus, @minTenderedSum, @maxTenderedSum, @publisher,@version,@borrowTitle
	#from	t_borrow	where id = in_bid;

	select a.id,a.hasInvestAmount,a.borrowStatus,a.minTenderedSum,a.maxTenderedSum,a.borrowWay,a.auditTime,a.excitationType,a.excitationSum ,
			   a.circulationNumber, a.version,a.annualRate,(a.annualRate/12),a.borrowAmount,a.smallestFlowUnit,
				 a.deadline,a.borrowTitle,a.publisher,a.isDayThe,b.username,a.frozen_margin,a.hasCirculationNumber,c.subscribe_status
	into @id,@hasInvestAmount,@borrowStatus,@minTenderedSum,@maxTenderedSum,@borrowWay,@auditTime,@excitationType,@excitationSum,
				@circulationNumber,@version,@annualRate,@monthRate,@borrowAmount,@smallestFlowUnit,
				@t_deadline,@borrowTitle,@publisher,@isDayThe,@borrowerName,@t_frozen_margin,@hasCirculationNumber,@subscribe_status
  from  t_borrow a left join t_user b on a.publisher=b.id left join t_borrow_type_log c on a.nid_log=c.identifier where a.id=in_bid;

	if @user_usableSum < in_invest_amount then
			set out_ret = -3;
			if	@subscribe_status = 1 then
				set	@max_subscribe 	= round(@user_usableSum/@smallestFlowUnit);
				set	@for_circulate	=	@circulationNumber - @hasCirculationNumber;
				if	@max_subscribe > @for_circulate then
					set	@max_subscribe	=	@for_circulate;
				end	if;
				set out_desc = concat('最多可认购[', @max_subscribe,']份');
			else
				set out_desc = concat('可用余额为[', @user_usableSum, ']元,不满足本轮投标,请充值');
			end	if;
			
			leave _return;
	end if;

	if @id is null then
		set out_ret = -5;
		set out_desc = '不存在的借款';

		leave _return;
	end if;

	if @borrowStatus <>	2 then
		set out_ret = -6;
		set out_desc = '非招标中的借款';

		leave _return;  
	end if;

	if @hasInvestAmount >= @borrowAmount  then
		set out_ret = -7;
		set out_desc = '投标异常的借款';

		leave _return;
	end if;

	if @publisher= in_uid then 
		set out_ret = -99;
			set out_desc = '不能投自己发布的标的';

			leave _return;
	end if ;

	if	@subscribe_status = 1 then
		if @circulationNumber < @hasCirculationNumber  then 
			set out_ret = -7;
			set out_desc = '投标异常的借款';

			leave _return;
		end if;
		if in_num <=0  then 
			set out_ret  = -16;	
			set out_desc = '非法的认购份数';

			leave _return;
		end if;
		
		 if  @circulationNumber - @hasCirculationNumber < in_num  then 
			set out_ret = -15;
			set out_desc = concat('剩余可认购[',(@circulationNumber - @hasCirculationNumber),']份');

			leave _return;
		end if ;
	else
		set @remainAmount = ifnull(@borrowAmount,0) - ifnull(@hasInvestAmount,0);

		if in_invest_amount > @remainAmount  then
			set out_ret = -8;
			set out_desc = '投标金额超过本轮剩余投标金额';

			leave _return;
		end if;

		#当剩余金额小于最新投标金额，直接跳过验证
		if @remainAmount	>	@minTenderedSum	then
			if in_invest_amount > @maxTenderedSum && @maxTenderedSum <>-1	then
				set out_ret = -9;
				set out_desc = '投标金额超过本轮最多投标金额';

				leave _return;
			end if;

			if in_invest_amount	<	@minTenderedSum  &&	@minTenderedSum	<>-1	then
				set out_ret = -10;
				set out_desc = '投标金额不足本轮最低投标金额';

				leave _return;
			end if;
		end if;

		set @total = in_invest_amount + @hasInvestAmount; #投资总额=本次投资金额+已投标金额
		if @total > @borrowAmount then
			set out_ret = -11;
			set out_desc = '投标金额超出借款总额';

			leave _return;
		end if;
	end if;
  

 start transaction;
 update t_borrow set version	=	version	+	1	  where id = in_bid  and borrowStatus = 2	and borrowAmount	>	hasInvestAmount	and version	=	@version;
 set @ret_update = ROW_COUNT();

		if @ret_update = 0 then
			rollback;

			set out_ret = -12;
			set out_desc = '抢投的借款';

			leave _return;
		end if;	 
	#更新借款信息中的已投资总额和数量
 update t_borrow set investNum	=	investNum	+	1	,hasInvestAmount = hasInvestAmount	+	in_invest_amount where id = in_bid  and borrowStatus = 2	and borrowAmount	>	hasInvestAmount	;
			#投资流转标处理
	if @borrowWay = 6 then 
			#得到转标借款费用 
			select feelog into @t_feelog from t_borrow where id = in_bid;
			#得到标的类型唯一标识符
			select nid_log into @borrow_nid  from t_borrow  where id = in_bid;
			select locan_fee,locan_month,locan_fee_month,day_fee into @locan_fee,@locan_month,@locan_fee_month,@day_fee from t_borrow_type_log  where identifier = @borrow_nid;
			#得到流转标借款费用
			if @isDayThe =2 then 
			set @flow_fee = in_invest_amount * (@day_fee / 365)/100;
			else 
				 if @t_deadline > @locan_month then 
					set @flow_fee = in_invest_amount * (@locan_fee + (@t_deadline - @locan_month) * @locan_fee_month)/100;
					else 
					set @flow_fee = in_invest_amount * @locan_fee /100;
					end if;
			end if;
		if @excitationType =2 then # 按固定金额奖励
			#计算出每一份要奖励的金额
			set @percopiesAward = @excitationSum / @circulationNumber;
			set @excutation = @percopiesAward * in_num ;
			set @realGetAmount = in_invest_amount - @excutation;

		elseif @excitationType =3 then   
			#按比例奖励 
			set @excutation = in_invest_amount * @excitationSum * 0.01;
			set @realGetAmount = in_invest_amount - @excutation;
		else 
			set @excutation = 0;
			set @realGetAmount = in_invest_amount;
		end if;
			#查询 还款 id
			select a.id,a.stillPrincipal,a.stillInterest,a.lateFI  into @t_r_id,@t_r_stilP,@t_r_stillI,@t_r_lateFI from t_repayment a left join t_borrow b on a.borrowId=b.id where a.borrowId = in_bid limit 0,1;
			#得到每月利息
			set @monthRate = @monthRate/100;
			set @monPayRate = @borrowAmount * @monthRate;
			select  f_parsing_json(@t_feelog,'investFeeRate') into @investFeeRate ;
		
	if @isDayThe =1 then #月标
			set @totalInterest = @monPayRate * @t_deadline;
			set @totalInterest = @totalInterest * (in_invest_amount / @borrowAmount);
	else  #天标
			set @totalInterest = @monPayRate * @t_deadline;
			set @totalInterest = @totalInterest * (in_invest_amount / @borrowAmount)/30.0;
  end if;
			set @iManageFee = @totalInterest * @investFeeRate;
			#到期收益总额
			set @earnAmount = in_invest_amount + @totalInterest-@iManageFee; 
			#得到还款本息
			set @stllPI = @t_r_stilP+@t_r_stillI+in_invest_amount+@totalInterest;
	
			#查询已投资总额是否小于等于借款总额，否则是错误投标,回滚操作
			set @t_id = 0;
			select IFNULL(id,0) into @t_id from t_borrow where hasInvestAmount <=borrowAmount and id =in_bid;
	if @t_id =0 then 
		rollback;
		
		set out_ret = -7;
		set out_desc = '投标异常的借款';

		leave _return;
  end if; 
	
		#增加投资记录
		insert into t_invest(investAmount, realAmount,investor, oriInvestor, investTime, 
						borrowId, monthRate,deadline,repayDate,recivedPrincipal,recievedInterest,isAutoBid,circulationForpayStatus) 
		values(in_invest_amount, in_invest_amount, in_uid, in_uid, in_invest_time, in_bid,
					@monthRate*100,@t_deadline,DATE_SUB(now(),INTERVAL -@t_deadline MONTH),in_invest_amount,@totalInterest,1,1);
		#得到刚增加的Id 投资ID
		select max(id) into @t_maxInvest_Id from t_invest; 
		#每笔投资相应的还款记录都会发生变化
		update t_repayment set stillPrincipal= stillPrincipal+ in_invest_amount,stillInterest = stillInterest+ @totalInterest where id= @t_r_id and repayStatus=1 ;
		#投资人投资成功资金扣除
		update t_user set usableSum =usableSum- in_invest_amount  where id = in_uid;
		
		#添加用户动态
		set t_content = '';
		set	t_content	=	f_link(in_basepath,in_bid,@borrowTitle,'','','');	
		set	t_content	=	concat('投标了借款[',	t_content,	']');
		insert into t_user_recorelist (userId,url,time)
		values(in_uid,t_content,now());

		set t_content = '';
		set	t_content	=	f_link(in_basepath,in_bid,@borrowTitle,'','','');	
		set	t_content	=	concat('您投标了流转标借款[',	t_content,	']扣除投标金额');
		#添加资金记录
		insert into t_fundrecord (userId, recordTime, operateType, fundMode, handleSum, usableSum, freezeSum, dueinSum,
				trader, dueoutSum, remarks, oninvest, paynumber, paybank, cost,spending,borrow_id)	select in_uid, now(), 655, f_moneyDecode(655), in_invest_amount,
				a.usableSum as usableSum, a.freezeSum as freezeSum, ifnull(sum(b.recivedPrincipal + b.recievedInterest - b.hasPrincipal - b.hasInterest),0),
		-1,0, t_content, 0, in_bid, '', 0 ,in_invest_amount,b.borrowId	from t_user a left join t_invest b on a.id = b.investor 
		where a.id = in_uid group by a.id;

		#流转标是否设置奖励
		if  @excutation >0 then #大于0为设置了奖励
		update t_user set usableSum =usableSum+ @excutation where id = in_uid;
		#添加借款奖励 
  	update t_invest set	reward	=	@excutation where id =	@t_maxInvest_Id;

		set t_content = '';
		set	t_content	=	f_link(in_basepath,in_bid,@borrowTitle,'','','');	
		set	t_content	=	concat('您投标了流转标借款[',	t_content,	']流转标奖励');
		#添加资金记录
		insert into t_fundrecord (userId, recordTime, operateType, fundMode, handleSum, usableSum, freezeSum, dueinSum,
				trader, dueoutSum, remarks, oninvest, paynumber, paybank, cost,income,borrow_id)	select in_uid, now(), 254, f_moneyDecode(254), @excutation,
				a.usableSum as usableSum, a.freezeSum as freezeSum, ifnull(sum(b.recivedPrincipal + b.recievedInterest - b.hasPrincipal - b.hasInterest),0),
			-1,0, t_content, 0, in_bid, '', 0,@excutation,b.borrowId	from t_user a left join t_invest b on a.id = b.investor 
		where a.id = in_uid group by a.id;
		end if;
			 
			#借款人操作资金
			update t_user set usableSum =usableSum + in_invest_amount  where id = @publisher;
			set t_content = '';
			set	t_content	=	f_link(in_basepath,in_bid,@borrowTitle,'','','');	
			set	t_content	=	concat('流转标借款[',	t_content,	']流转借款成功收款');
			#添加资金记录
			insert into t_fundrecord (userId, recordTime, operateType, fundMode, handleSum, usableSum, freezeSum, dueinSum,
			trader, dueoutSum, remarks, oninvest, paynumber, paybank, cost , income,borrow_id)	select @publisher, now(), 102, f_moneyDecode(102), in_invest_amount,
			a.usableSum as usableSum, a.freezeSum as freezeSum, ifnull(sum(b.recivedPrincipal + b.recievedInterest - b.hasPrincipal - b.hasInterest),0),
			-1,0, t_content, 0, @publisher, '', 0 ,in_invest_amount,b.borrowId	from t_user a left join t_invest b on a.id = b.investor where a.id = @publisher group by a.id;
			
			set @t_manage_fee =  @flow_fee;
			#格式化借款管理费
			set @t_manage_fee = round(@t_manage_fee,2); 
		if @t_manage_fee >0 then 
			#借款人操作资金
			update t_user set usableSum =usableSum - @t_manage_fee  where id = @publisher;
			#添加资金记录
			insert into t_fundrecord (userId, recordTime, operateType, fundMode, handleSum, usableSum, freezeSum, dueinSum,
			trader, dueoutSum, remarks, oninvest, paynumber, paybank, cost, spending,borrow_id)	select @publisher, now(), 601, f_moneyDecode(601), @t_manage_fee,
			a.usableSum as usableSum, a.freezeSum as freezeSum, ifnull(sum(b.recivedPrincipal + b.recievedInterest - b.hasPrincipal - b.hasInterest),0),
			-1,0, '成功收款,扣除借款管理费', 0,  @publisher, '', 0 ,@t_manage_fee,b.borrowId	from t_user a left join t_invest b on a.id = b.investor where a.id = @publisher group by a.id;
		end if;
		if @excutation > 0 then 
			#借款人操作资金 
			update t_user set usableSum =usableSum - @excutation  where id = @publisher;
			set t_content = '';
			set	t_content	=	f_link(in_basepath,in_bid,@borrowTitle,'','','');	
			set	t_content	=	concat('流转标借款[',	t_content,	']流转借款成功扣除奖励金额');
			#添加资金记录
			insert into t_fundrecord (userId, recordTime, operateType, fundMode, handleSum, usableSum, freezeSum, dueinSum,
			trader, dueoutSum, remarks, oninvest, paynumber, paybank, cost, spending,borrow_id)	select @publisher, now(), 751, f_moneyDecode(751), @excutation,
			a.usableSum as usableSum, a.freezeSum as freezeSum, ifnull(sum(b.recivedPrincipal + b.recievedInterest - b.hasPrincipal - b.hasInterest),0),
			-1,0, t_content, 0, in_bid, '', 0 ,@excutation,b.borrowId	from t_user a left join t_invest b on a.id = b.investor where a.id = @publisher group by a.id;
		end if;
			set @recivedI = in_invest_amount * @monthRate * @t_deadline;
			#添加还款记录
			insert into t_invest_repayment (repayId,repayPeriod,repayDate,recivedPrincipal,recivedInterest,
							principalBalance,interestBalance,invest_id,`owner`,ownerlist,iManageFeeRate,iManageFee,repayStatus,circulationForpayStatus)
			values(@t_r_id,'1/1',date_format(DATE_SUB(now() , INTERVAL -@t_deadline MONTH) ,'%Y-%m-%d'),in_invest_amount,@totalInterest,0,0,
						@t_maxInvest_Id,in_uid,in_uid,@investFeeRate,@investFeeRate * @totalInterest,1,1);

			#添加投资记录 
			#得到模板 发送给投资人
			#站内信
			set	t_mail_template	=	f_get_notice_template('tender_roam_success');

			set	t_mail_template = replace(t_mail_template,	'[monthRate]',round(@monthRate*100,2));
			set	t_mail_template = replace(t_mail_template,	'[deadline]',  @t_deadline);
			set	t_mail_template = replace(t_mail_template,	'[realAmount]', in_invest_amount);
			set	t_mail_template = replace(t_mail_template,	'[totalInterest]', round(@totalInterest,2));
			set	t_mail_template = replace(t_mail_template,	'[iManageFee]', round(@iManageFee,2));
			set	t_mail_template = replace(t_mail_template,	'[earnAmount]', round(@earnAmount,2));
