			from t_user a left join t_invest b on a.id = b.investor where a.id = in_userid group by a.id;
			set t_mail_template = f_get_notice_template('recharge_success');			set t_mail_template = replace(t_mail_template, '[date]', NOW());			set t_mail_template = replace(t_mail_template, '[money]', in_money);			set t_mail_template = replace(t_mail_template, '[<br/>]', '<br />');			set t_email_template = f_get_notice_template('s_recharge_success');			set t_email_template = replace(t_email_template, '[date]', NOW());			set t_email_template = replace(t_email_template, '[money]', in_money);			set t_email_template = replace(t_email_template, '[<br/>]', '<br />');			set @username = '';			set @email = '';			set @telphone = '';			select a.cellphone, b.username, b.email into @telphone, @username, @email from t_user b left join			t_person a on a.userId = b.id where b.id = in_userid;			set t_sms_template = f_get_notice_template('s_recharge_success');			set t_sms_template = replace(t_sms_template, '[userName]', @username);			set t_sms_template = replace(t_sms_template, '[date]', NOW());			set t_sms_template = replace(t_sms_template, '[money]', in_money);			set t_sms_template = replace(t_sms_template, '[<br/>]', '<br />');			insert into t_notice_task(user_id, sendtitle, s_nid, username, email, telphone, mail_info, email_info, sms_info, operate_id, add_time, operate_identify)			values(in_userid, '充值成功报告', 'showSucEnable', @username, @email, @telphone, t_mail_template, t_email_template, t_sms_template, in_userid, now(), 'recharge');

	if (last_insert_id() <= 0) then 
		rollback;
		set returnValue = -99999;
		set returnDescription = '未知错误'; 

		leave _return;
