DELIMITER $$

/*!50003 CREATE  PROCEDURE `p_userInfo_update`(IN `in_realname` varchar(20),IN `in_cellPhone` varchar(20),IN `in_sex` varchar(20),IN `in_birthday` varchar(20),IN `in_highestEdu` varchar(20),IN `in_eduStartDay` varchar(20),IN `in_school` varchar(20),IN `in_maritalStatus` varchar(20),IN `in_hasChild` varchar(20),IN `in_hasHourse` varchar(20),IN `in_hasHousrseLoan` varchar(20),IN `in_hasCar` varchar(20),IN `in_hasCarLoan` varchar(20),IN `in_nativePlacePro` bigint,IN `in_nativePlaceCity` bigint,IN `in_registedPlacePro` bigint,IN `in_registedPlaceCity` bigint,IN `in_address` varchar(20),IN `in_telephone` varchar(20),IN `in_personalHead` varchar(255),IN `in_userId` bigint,IN `in_idNo` varchar(20),IN `in_username` varchar(20),IN `in_lastIP` varchar(50),IN `in_num` varchar(20),OUT `out_ret` int,OUT `out_desc` varchar(255))
_return:BEGIN

	declare	_error	int	default	0;	#异常标记

	declare continue handler for sqlexception set _error	=	1;	#sqlexception返回值



	set out_ret = -1;

	set out_desc = '执行失败';

	SET @u_num = 0;
	SET @p_num = 0;
	
	SELECT COUNT(1) INTO @u_num from t_person WHERE userId = in_userId;
	SELECT COUNT(1) INTO @p_num FROM t_phone_binding_info WHERE userId = in_userId;

	start transaction;
	
	IF @u_num = 0 THEN
	
	INSERT into t_person (realName, cellphone, sex, birthday, highestEdu, eduStartDay, school,
						maritalStatus, hasChild, hasHourse, hasHousrseLoan, hasCar, hasCarLoan, nativePlacePro,
