DELIMITER $$

/*!50003 CREATE  PROCEDURE `p_user_login`(IN `in_userName` varchar(50),IN `in_passWord` varchar(50),IN `in_lastIP` varchar(50),OUT `out_ret` int,OUT `out_desc` varchar(50))
_return:BEGIN
declare _error int default 0; #异常标记#
declare continue handler for sqlexception set _error = 1; #sqlexception返回值
set out_ret = -1;
set out_desc = '执行失败';
set @u_id = -1;
set @u_username = null;
set @u_rating = 0;
set @u_enable= 0;
set @u_login_count = 0;
set @u_loginNum = 0;
if in_userName = "" then 
set out_ret = -2 ;
set out_desc = '用户名或邮箱或手机号码为空';
leave _return;
end if;
if in_userName = "" then 
set out_ret = -3 ;
set out_desc = '密码为空';
leave _return;
end if;
select u.id, u.username, u.rating, u.`enable`, u.loginCount into @u_id, @u_username, @u_rating, @u_enable, @u_login_count from t_user u left           join t_person p on u.id = p.userId where (u.email = in_userName or u.username = in_userName or p.cellphone = in_userName) and u.`password` = in_passWord;
set @u_id = ifnull(@u_id,-1);
set @u_rating = ifnull(@u_rating,0);
set @u_enable= ifnull(@u_enable,0);
set @u_login_count = ifnull(@u_login_count,0);
set @u_loginNum = ifnull(@u_loginNum,0);
if @u_id = -1 then 
set out_ret = -4 ;
set out_desc = '用户名或密码错误';
leave _return;
end if;
if @u_enable = 2 then
set out_ret = -5 ;
set out_desc = '用户被禁用';
leave _return;
end if;
start transaction;
if @u_id > 0 then
call p_user_score(@u_id,@u_username,in_lastIP, @ret, @ret_desc);
end if;
if _error <> 0 then
rollback;
set out_ret = -11;
set out_desc = '执行异常';
leave _return;
else 
commit;
set out_ret = @u_id;
set out_desc = '';
end if;
END */$$
